"use strict";

var Drivers =
{
    "stepper": require("./Stepper.js")
};

module.exports =
{
    drivers: Object.keys(Drivers),

    driver: function(opts)
    {
        for (var d in Drivers)
        {
            if (opts.driver === d)
            {
                return new Drivers[d](opts);
            }
        }

        return null;
    }
};
